import React ,{ useState } from "react";
import { useLocation, Navigate } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import alertContext from "../context/alert/alertContext";
import { useContext } from "react";
export default function Checkout() {
  
    const { showAlert } = useContext(alertContext);
    const navigate = useNavigate();
      const [otpSent, setOtpSent] =useState(false);
  const [otp, setOtp] = useState(["", "", "", "", "", ""]);
     const location = useLocation();
  const data = location.state;

  if (!data) {
    return <Navigate to="/cart" />;
  }
   const handleChange = (value, index,) => {
    if (!/^\d?$/.test(value)) return;

    const newOtp = [...otp];
    newOtp[index] = value;
    setOtp(newOtp);
  };


  const {
    cartItems,
    subtotal,
    shippingCost,
    deliveryType,
    total,
  } = data;

 const sendOtp = async () => {
  

  const email = document.getElementById("email").value;

  if (!email) {
    alert("Enter email first");
    return;
  }

  try {
    const res = await fetch("http://localhost:5000/api/order/sendotp", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email })
    });

    const data = await res.json();

    if (res.ok) {
      alert("OTP sent to email");
      setOtpSent(true);
    } else {
      alert(data.error || "OTP failed");
    }
  } catch (err) {
    console.error(err);
    alert("OTP error");
  }
};

  const handleCheckout = async (e) => {
     e.preventDefault();
const finalOtp = otp.join("");
   const email = document.getElementById("email").value;



    const res = await fetch("http://localhost:5000/api/order/verify-otp", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
     body: JSON.stringify({ email,finalOtp: otp }),
    });

 

    if (res.ok) {
      showAlert("OTP verified","success");
      const customer = {
   firstName: document.getElementById("firstName").value,
  lastName: document.getElementById("lastName").value,
    email: document.getElementById("email").value,
    phone: document.getElementById("phone").value,
    address: document.getElementById("address").value,
    apartment: document.getElementById("apartment").value,
    city: document.getElementById("city").value,
    state: document.getElementById("state").value,
    pin: document.getElementById("pin").value,
    country: "India"
  };

  // Build order payload
  const payload = {
 
    customer,
    cartItems,
    subtotal,
    shippingCost,
    deliveryType,
    total,
    paymentMethod: "Card"
  };

  try {
    const res = await fetch("http://localhost:5000/api/order/create", {
      method: "POST",
      headers: { "Content-Type": "application/json" ,   "auth-token": localStorage.getItem("token"),},
      body: JSON.stringify(payload,email)
    });
console.log("order");
    const data = await res.json();
    if (data.success) {
      alert("Order placed successfully!");
      
    } else {
      alert("Failed to place order");
    }
  } catch (err) {
    console.error(err);
    alert("Error placing order");
  }
       
  
    } else {
      showAlert("Invalid OTP","danger");
    }
 
  /*const customer = {
   firstName: document.getElementById("firstName").value,
  lastName: document.getElementById("lastName").value,
    email: document.getElementById("email").value,
    phone: document.getElementById("phone").value,
    address: document.getElementById("address").value,
    apartment: document.getElementById("apartment").value,
    city: document.getElementById("city").value,
    state: document.getElementById("state").value,
    pin: document.getElementById("pin").value,
    country: "India"
  };

  // Build order payload
  const payload = {
 
    customer,
    cartItems,
    subtotal,
    shippingCost,
    deliveryType,
    total,
    paymentMethod: "Card"
  };

  try {
    const res = await fetch("http://localhost:5000/api/order/create", {
      method: "POST",
      headers: { "Content-Type": "application/json" ,   "auth-token": localStorage.getItem("token"),},
      body: JSON.stringify(payload)
    });
console.log("order");
    const data = await res.json();
    if (data.success) {
      alert("Order placed successfully!");
      
    } else {
      alert("Failed to place order");
    }
  } catch (err) {
    console.error(err);
    alert("Error placing order");
  }*/

  

};

  return (
    
    <div
      className="container-fluid py-5"
      style={{
        background: "linear-gradient(135deg, #f6f8fb, #eef1f6)",
        minHeight: "100vh",
      }}
    >
      <div className="row justify-content-center">
        <div className="col-lg-6 col-xl-6">
          <div className="bg-white rounded-4 shadow-lg p-4 p-md-5">

        <h3 className="fw-bold mb-1 text-center">Checkout</h3>
           

         
            <section className="mb-4">
              <h6 className="fw-bold mb-3">Contact</h6>
              <input id="email"
                className="form-control form-control-lg mb-3"
                placeholder="Email address"
              />
            </section>

          
            <section className="mb-4"> 
              <h6 className="fw-bold mb-3"> Delivery</h6>
              <p className="text-muted small mb-3">
  Delivery available within India only
</p>

              <select className="form-select form-select-lg mb-3">
                <option>India</option>
              </select>

              <div className="row">
                <div className="col">
                  <input id="firstName"
                    className="form-control form-control-lg mb-3"
                    placeholder="First name"
                  />
                </div>
                <div className="col">
                  <input id="lastName"
                    className="form-control form-control-lg mb-3"
                    placeholder="Last name"
                  />
                </div>
              </div>

              <input id="address" 
                className="form-control form-control-lg mb-3"
                placeholder="Address" required
              />
              <input   id="apartment"
                className="form-control form-control-lg mb-3"
                placeholder="Apartment, suite, etc. (optional)"
              />

              <div className="row">
                <div className="col-md-5">
                  <input  id="city"
                    className="form-control form-control-lg mb-3"
                    placeholder="City"
                  />
                </div>
                <div className="col-md-4">
                  <input id="state"
                    className="form-control form-control-lg mb-3"
                    placeholder="State"
                  />
                </div>
                <div className="col-md-3">
                  <input id="pin" 
                    className="form-control form-control-lg mb-3"
                    placeholder="PIN"
                  />
                </div>
              </div>

              <input id="phone" 
                className="form-control form-control-lg"
                placeholder="Phone number"
              />
            </section>

       
         

            <section className="mb-4">
              <h6 className="fw-bold mb-3"> Payment</h6>
              <div className="border rounded-3 p-3 bg-light">
                <input
                  className="form-control form-control-lg mb-3"
                  placeholder="Card number"
                />

                <div className="row">
                  <div className="col">
                    <input
                      className="form-control form-control-lg mb-3"
                      placeholder="MM / YY"
                    />
                  </div>
                  <div className="col">
                    <input
                      className="form-control form-control-lg mb-3"
                      placeholder="CVV"
                    />
                  </div>
                </div>

                <input
                  className="form-control form-control-lg"
                  placeholder="Name on card"
                />
              </div>
            </section>

       
        {!otpSent ? (
  <button
    onClick={sendOtp}
    className="btn btn-dark w-100 py-3 fw-bold rounded-3"
  >
    Pay Now
  </button>
) : (
  <>
   
     <div className="d-flex justify-content-between mb-3">
        {otp.map((digit, index) => (
          <input
            key={index}
            type="text"
            maxLength="1"
            className="form-control text-center mx-1"
            style={{ width: "45px" }}
            value={digit}
            onChange={(e) => handleChange(e.target.value, index)}
            required
          />
        ))}
      </div>

    <button
      onClick={handleCheckout}
      className="btn btn-success w-100 py-3 fw-bold rounded-3"
    >
      Confirm Order
    </button>
  </>
)}


            <p className="text-center text-muted mt-3 small">
