import React, { useState } from "react";
import "./ProductItem.css";

export default function ProductItem({ product }) {
  
  const variants = product?.variants || [];

  const [selectedVariant, setSelectedVariant] = useState(null);
  const [selectedSize, setSelectedSize] = useState("");

  if (!product) return null;

  const { name, price, tag, rating = 5 } = product;

  const handleColorClick = (variant) => {
    setSelectedSize("");
    setSelectedVariant(variant);

  };
  const handleAddToCart = () => {
  if (!selectedVariant || !selectedSize) return;

  const sizeIndex = selectedVariant.sizes.findIndex(
    s => s.size === selectedSize
  );

  if (sizeIndex === -1) return;

  // ❌ If out of stock
  if (selectedVariant.sizes[sizeIndex].quantity === 0) return;

  // ✅ Reduce quantity (UI only)
  const updatedVariants = variants.map(v => {
    if (v.color === selectedVariant.color) {
      return {
        ...v,
        sizes: v.sizes.map(s =>
          s.size === selectedSize
            ? { ...s, quantity: s.quantity - 1 }
            : s
        )
      };
    }
    return v;
  });

  setSelectedVariant(updatedVariants.find(v => v.color === selectedVariant.color));

  // ✅ Store in localStorage (cart)
  const cartItem = {
    productId: product._id,
    name: product.name,
    price: product.price,
    color: selectedVariant.color,
    image: selectedVariant.images[0],
    size: selectedSize,
    quantity: 1
  };

  const cart = JSON.parse(localStorage.getItem("cart")) || [];

  const existingIndex = cart.findIndex(
    item =>
      item.productId === cartItem.productId &&
      item.color === cartItem.color &&
      item.size === cartItem.size
  );

  if (existingIndex > -1) {
    cart[existingIndex].quantity += 1;
  } else {
    cart.push(cartItem);
  }

  localStorage.setItem("cart", JSON.stringify(cart));
};


  const handleSizeClick = (size) => {
    setSelectedSize(size);
  };

  return (
    <div className="col-sm-6 col-md-4 col-lg-3">
      <div className="card product-card h-100 border-0 shadow-sm">
        <div className="position-relative">
         <img
  src={
    selectedVariant?.images?.length > 0
      ? selectedVariant.images[0]
      : variants?.[0]?.images?.[0] || "" 
  }
  className="card-img-top product-img"
  alt={name}
/>

          {tag && (
            <span
              className={`badge ${
                tag.toLowerCase() === "sale" ? "bg-danger" : "bg-dark"
              } position-absolute top-0 start-0 m-2`}
            >
              {tag}
            </span>
          )}
        </div>

        <div className="card-body text-center">
          <h5 className="card-title">{name}</h5>

          <div className="text-warning mb-2">
            {[...Array(rating)].map((_, i) => (
              <span key={i} className="fas fa-star"></span>
            ))}
          </div>

          <p className="fw-bold">${price}</p>

          <div className="d-flex justify-content-center mb-2">
            {variants.map((v, i) => (
              <span
                key={i}
                className={`color-circle mx-1 ${
                  selectedVariant?.color === v.color ? "selected" : ""
                }`}
                style={{ backgroundColor: v.color }}
                title={v.color}
                onClick={() => handleColorClick(v)}
              />
            ))}
          </div>

          <div className="d-flex justify-content-center gap-2 flex-wrap mb-2">
            {selectedVariant?.sizes.map((s, i) => (
              <span
                key={i}
                className={`size-box ${
                  selectedSize === s.size ? "selected" : ""
                } ${s.quantity === 0 ? "out-of-stock" : ""}`}
                onClick={() => s.quantity > 0 && handleSizeClick(s.size)}
                title={`Qty: ${s.quantity}`}
              >
                {s.size}
              </span>
            ))}
          </div>

        
              
     {selectedSize && selectedVariant && (
  <span
    className={`badge mt-2 ${
      selectedVariant.sizes.find((s) => s.size === selectedSize)?.quantity > 6
      ? "border text-success bg-transparent" // green outline, transparent background
        : "bg-danger text-white"
    }`}
  >
    Available: {selectedVariant.sizes.find((s) => s.size === selectedSize)?.quantity ?? 0}
  </span>
)}
<button
  className="btn btn-dark w-100 mt-2"
  disabled={
    !selectedVariant ||
    !selectedSize ||
    selectedVariant.sizes.find(s => s.size === selectedSize)?.quantity === 0
  }
  onClick={() => handleAddToCart()}
>
  Add to Cart
</button>



        </div>
      </div>
    </div>
  );
}
