import React, { useState } from "react";
import "./ProductItem.css";

export default function ProductItem({ product }) {
  const [selectedVariant, setSelectedVariant] = useState(null);
  const [selectedSize, setSelectedSize] = useState("");
  const variantOutOfStock = selectedVariant && !selectedVariant.isActive;
const sizeOutOfStock =
  selectedVariant &&
  selectedSize &&
  selectedVariant.sizes.find((s) => s.size === selectedSize)?.quantity === 0;

  if (!product) return null;

  const variants = product.variants || [];
  const { name, price, tag, rating = 5 } = product;

const handleColorClick = (variant) => {
 
  setSelectedVariant(variant);
  setSelectedSize(""); 
};

const handleSizeClick = (size) => {
  setSelectedSize(size);
};

const handleAddToCart = async () => {
  if (!selectedVariant || !selectedSize) return;

  const cartItem = {
    productId: product._id,
    color: selectedVariant.color,
    size: selectedSize,
    image: selectedVariant.images,
    quantity: 1,
    price: product.price,
  };

  const res = await fetch("http://localhost:5000/api/cart/addcart", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      "auth-token": localStorage.getItem("token"),
    },
    body: JSON.stringify(cartItem),
  });

  const data = await res.json();
 if (data.success) {
 const updatedProductRes = await fetch(
  `http://localhost:5000/api/product/fetchproduct?id=${product._id}`
);
const updatedProductData = await updatedProductRes.json();

setSelectedVariant(
  updatedProductData.product.variants.find(
    v => v.color === selectedVariant.color
  )
);

}

};



return (
  <div className="col-sm-6 col-md-4 col-lg-3">
    <div className="card product-card h-100 border-0 shadow-sm">
      <div className="position-relative">
        <img
          src={selectedVariant?.images || variants?.[0]?.images || ""}
          className="card-img-top product-img"
          alt={name}
          style={{
            opacity: variantOutOfStock || sizeOutOfStock ? 0.5 : 1, // dim image if unavailable
          }}
        />

        {tag && (
          <span
            className={`badge ${
              tag.toLowerCase() === "sale" ? "bg-danger" : "bg-dark"
            } position-absolute top-0 start-0 m-2`}
          >
            {tag}
          </span>
        )}

        {(variantOutOfStock || sizeOutOfStock) && (
          <span className="badge bg-danger position-absolute top-50 start-50 translate-middle">
            Not Available
          </span>
        )}
      </div>

      <div className="card-body text-center">
        <h5 className="card-title">{name}</h5>
        <p className="fw-bold">â‚¹{price}</p>

        {/* Colors */}
        <div className="d-flex justify-content-center mb-2">
          {variants.map((v, i) => (
            <span
              key={i}
              className={`color-circle mx-1 ${
                selectedVariant?.color === v.color ? "selected" : ""
              } ${!v.isActive ? "out-of-stock" : ""}`}
              style={{ backgroundColor: v.color }}
              onClick={() => v.isActive && handleColorClick(v)}
            />
          ))}
        </div>

        {/* Sizes */}
        <div className="d-flex justify-content-center gap-2 flex-wrap mb-2">
          {selectedVariant?.sizes.map((s, i) => (
            <span
              key={i}
              className={`size-box ${
                selectedSize === s.size ? "selected" : ""
              } ${s.quantity === 0 ? "out-of-stock" : ""}`}
              onClick={() => handleSizeClick(s.size)}
            >
              {s.size}
            </span>
          ))}
        </div>

        {/* Availability */}
        {selectedVariant && selectedSize && (
          <span className="badge bg-secondary mt-2">
            Available:{" "}
            {selectedVariant.sizes.find((s) => s.size === selectedSize)
              ?.quantity ?? 0}
          </span>
        )}

        {/* Add to Cart */}
        <button
          className="btn btn-dark w-100 mt-2"
          disabled={!selectedVariant || !selectedSize || variantOutOfStock || sizeOutOfStock}
          onClick={handleAddToCart}
        >
          Add to Cart
        </button>
      </div>
    </div>
  </div>
);
}
