import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function AdminMessageDetail() {
  const { id } = useParams();
  const [message, setMessage] = useState(null);
  const [reply, setReply] = useState("");

  useEffect(() => {
    fetch(`http://localhost:5000/api/admin/messages/${id}`, {
      headers: { "auth-token": localStorage.getItem("token") }
    })
      .then(res => res.json())
      .then(data => setMessage(data.message));
  }, [id]);

  const sendReply = async () => {
    await fetch(
      `http://localhost:5000/api/admin/messages/reply/${id}`,
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "auth-token": localStorage.getItem("token")
        },
        body: JSON.stringify({ reply })
      }
    );

    setReply("");
    window.location.reload();
  };

  if (!message) return null;

  return (
    <div className="container py-4">
      <h4>{message.fullname}</h4>
      <p>{message.message}</p>

      <hr />

      <h6>Previous Replies</h6>
      {message.replies.map((r, i) => (
        <div key={i} className="bg-light p-2 mb-2 rounded">
          <p className="mb-1">{r.message}</p>
          <small className="text-muted">
            {new Date(r.repliedAt).toLocaleString()}
          </small>
        </div>
      ))}

      <textarea
        className="form-control mt-3"
        placeholder="Write reply..."
        value={reply}
        onChange={e => setReply(e.target.value)}
      />

      <button className="btn btn-dark mt-2" onClick={sendReply}>
        Send Reply
      </button>
    </div>
  );
}
