import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './ProductItem.css';

export default function ProductItem() {
  const products = [
    {
      name: "Winter Sweater",
      image: "https://images.pexels.com/photos/54203/pexels-photo-54203.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
      price: 60,
      tag: "sale",
      rating: 5,
      colors: ["Red", "Green", "Blue"],
      sizes: ["S", "M", "L", "XL"],
    },
    {
      name: "Denim Dress",
      image: "https://images.pexels.com/photos/6764040/pexels-photo-6764040.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
      price: 55,
      tag: "out of stock",
      rating: 4,
      colors: ["Black", "Gray"],
      sizes: ["XS", "S", "M"],
    },
    // Add more products here
  ];

  return (
    <div className="container py-5">
      <div className="row g-4">
        {products.map((product, index) => {
          const { name, image, price, tag, rating, colors, sizes } = product;
          return (
            <div key={index} className="col-sm-6 col-md-4 col-lg-3">
              <div className="card product-card h-100 border-0 shadow-sm">
                {/* Product Image */}
                <div className="position-relative">
                  <img src={image} className="card-img-top product-img" alt={name} />

                  {/* Tag */}
                  {tag && (
                    <span
                      className={`position-absolute top-0 start-0 m-2 px-2 py-1 text-white rounded ${
                        tag.toLowerCase() === "sale" ? "bg-danger" : "bg-dark"
                      }`}
                    >
                      {tag}
                    </span>
                  )}

                  {/* Hover Icons */}
                  <div className="product-icons d-flex justify-content-center align-items-center position-absolute w-100 h-100 top-0 start-0">
                    <span className="fas fa-expand me-2"></span>
                    <span className="far fa-heart me-2"></span>
                    <span className="fas fa-shopping-bag"></span>
                  </div>
                </div>

                {/* Product Info */}
                <div className="card-body text-center">
                  <h5 className="card-title">{name}</h5>

                  {/* Ratings */}
                  <div className="text-warning mb-2">
                    {[...Array(rating)].map((_, i) => (
                      <span key={i} className="fas fa-star"></span>
                    ))}
                  </div>

                  <p className="card-text fw-bold">${price}</p>

                  {/* Colors */}
                  <div className="d-flex justify-content-center mb-2">
                    {colors.map((color, i) => (
                      <span
                        key={i}
                        className="color-circle mx-1"
                        style={{ backgroundColor: color.toLowerCase() }}
                        title={color}
                      ></span>
                    ))}
                  </div>

                  {/* Sizes */}
                  <div className="d-flex justify-content-center gap-2">
                    {sizes.map((size, i) => (
                      <span key={i} className="size-box">
                        {size}
                      </span>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
