import React, { useState } from "react";
import ProductItem from "../pages/ProductItem";
import "../App.css";

export default function Product({ category, categories }) {
  const [products, setProducts] = useState([]);

  const handleClick = async (type) => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/products/fetchproduct?category=${category}&type=${type}`
      );

      const data = await res.json();

      if (data.success) {
        setProducts(data.products); // products is an array
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="container py-5">
      <header className="text-center mb-5">
        <h1 className="display-5 fw-bold">Shop by Category</h1>
        <p className="text-muted fs-5">
          Discover your favorite styles â€” timeless and effortless.
        </p>
      </header>

      {/* Category Cards */}
      <div className="row g-3 mb-5">
        {categories.map((cat, index) => (
          <div key={index} className="col-12 col-md-6 col-lg-3">
            <div
              className="card h-100 shadow-sm border-0"
              style={{ cursor: "pointer" }}
              onClick={() => handleClick(cat.link)}
            >
              <div className="ratio ratio-4x3 position-relative overflow-hidden">
                <img
                  src={cat.image}
                  className="card-img-top"
                  alt={cat.name}
                  style={{ objectFit: "cover", transition: "transform 0.3s" }}
                />
                <div className="position-absolute top-0 start-0 w-100 h-100 d-flex justify-content-center align-items-center bg-dark bg-opacity-25 opacity-0 hover-opacity">
                  <button className="btn btn-light fw-bold">Shop Now</button>
                </div>
              </div>

              <div className="card-body text-center">
                <h5 className="card-title">{cat.name}</h5>
                <p className="card-text text-uppercase text-muted">
                  Explore {cat.name.toLowerCase()}
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Fetched Products */}
      {products.length > 0 && (
        <div className="row g-4">
          {products.map((product) => (
            <ProductItem key={product._id} product={product} />
          ))}
        </div>
      )}
    </div>
  );
}
