    state: document.getElementById("state").value,
    pin: document.getElementById("pin").value,
    country: "India"
  };

  // Build order payload
  const payload = {
 
    customer,
    cartItems,
    subtotal,
    shippingCost,
    deliveryType,
    total,
    paymentMethod: "Card"
  };

  try {
