      method: "POST",
      headers: { "Content-Type": "application/json" ,   "auth-token": localStorage.getItem("token"),},
      body: JSON.stringify(payload)
    });
console.log("order");
    const data = await res.json();
    if (data.success) {
      alert("Order placed successfully!");
      
    } else {
      alert("Failed to place order");
    }
  } catch (err) {
    console.error(err);
    alert("Error placing order");
  }*/

  

};

  return (
    
    <div
      className="container-fluid py-5"
      style={{
        background: "linear-gradient(135deg, #f6f8fb, #eef1f6)",
        minHeight: "100vh",
      }}
    >
      <div className="row justify-content-center">
        <div className="col-lg-6 col-xl-6">
          <div className="bg-white rounded-4 shadow-lg p-4 p-md-5">

        <h3 className="fw-bold mb-1 text-center">Checkout</h3>
           

         
            <section className="mb-4">
              <h6 className="fw-bold mb-3">Contact</h6>
              <input id="email"
                className="form-control form-control-lg mb-3"
                placeholder="Email address"
              />
            </section>

          
            <section className="mb-4"> 
              <h6 className="fw-bold mb-3"> Delivery</h6>
              <p className="text-muted small mb-3">
  Delivery available within India only
</p>

              <select className="form-select form-select-lg mb-3">
                <option>India</option>
              </select>

              <div className="row">
                <div className="col">
                  <input id="firstName"
                    className="form-control form-control-lg mb-3"
                    placeholder="First name"
                  />
                </div>
                <div className="col">
                  <input id="lastName"
                    className="form-control form-control-lg mb-3"
                    placeholder="Last name"
                  />
                </div>
              </div>

              <input id="address" 
                className="form-control form-control-lg mb-3"
                placeholder="Address" required
              />
              <input   id="apartment"
                className="form-control form-control-lg mb-3"
                placeholder="Apartment, suite, etc. (optional)"
              />

              <div className="row">
                <div className="col-md-5">
                  <input  id="city"
                    className="form-control form-control-lg mb-3"
                    placeholder="City"
                  />
                </div>
                <div className="col-md-4">
                  <input id="state"
                    className="form-control form-control-lg mb-3"
                    placeholder="State"
                  />
                </div>
                <div className="col-md-3">
                  <input id="pin" 
                    className="form-control form-control-lg mb-3"
                    placeholder="PIN"
                  />
                </div>
              </div>

              <input id="phone" 
                className="form-control form-control-lg"
                placeholder="Phone number"
              />
            </section>

       
         

            <section className="mb-4">
              <h6 className="fw-bold mb-3"> Payment</h6>
              <div className="border rounded-3 p-3 bg-light">
                <input
                  className="form-control form-control-lg mb-3"
                  placeholder="Card number"
                />

                <div className="row">
                  <div className="col">
                    <input
                      className="form-control form-control-lg mb-3"
                      placeholder="MM / YY"
                    />
                  </div>
                  <div className="col">
                    <input
                      className="form-control form-control-lg mb-3"
                      placeholder="CVV"
                    />
                  </div>
                </div>

                <input
                  className="form-control form-control-lg"
                  placeholder="Name on card"
                />
              </div>
            </section>

       
        {!otpSent ? (
  <button
    onClick={sendOtp}
    className="btn btn-dark w-100 py-3 fw-bold rounded-3"
  >
    Pay Now
  </button>
) : (
  <>
    <input
      className="form-control my-3"
      placeholder="Enter OTP"
      value={otp}
      onChange={(e) => setOtp(e.target.value)}
    />

    <button
      onClick={handleCheckout}
      className="btn btn-success w-100 py-3 fw-bold rounded-3"
    >
      Confirm Order
    </button>
  </>
)}


            <p className="text-center text-muted mt-3 small">
              Your payment is secure and encrypted
            </p>

          </div>
        </div>
      </div>
    </div>
  );
}
