import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../App.css";

export default function Product({ category }) {
  const navigate = useNavigate();
  const [types, setTypes] = useState([]);

  const isAdmin =
    localStorage.getItem("role") === "admin" ||
    localStorage.getItem("isAdmin") === "true";

  useEffect(() => {
    const fetchTypes = async () => {
      try {
        const res = await fetch(
          `http://localhost:5000/api/type/category/${category}`
        );
        const data = await res.json();
        setTypes(data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchTypes();
  }, [category]);

  // ✅ DELETE TYPE (ADMIN)
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this category?")) return;

    try {
      await fetch(`http://localhost:5000/api/type/${id}`, {
        method: "DELETE",
        headers: {
          "auth-token": localStorage.getItem("token"),
        },
      });

      // remove from UI instantly
      setTypes((prev) => prev.filter((type) => type._id !== id));
    } catch (err) {
      console.error("Delete error:", err);
    }
  };

  return (
    <div className="container py-6 mt-6">

      {/* Header */}
      <header className="text-center mb-5">
        <h1 className="display-5 fw-bold">Shop by Category</h1>
        <p className="text-muted fs-5">
          Discover your favorite styles — timeless and effortless.
        </p>
      </header>

      {/* Categories */}
      <div className="row g-4 mb-5">
        {types.map((type) => (
          <div key={type._id} className="col-6 col-md-4 col-lg-3">
            <div className="card h-100 shadow-sm border-0 product-card">

              {/* Image */}
              <div
                style={{ cursor: "pointer" }}
                onClick={() =>
                  navigate(`/product/${category}/${type.name}`)
                }
              >
                <img
                  src={type.image}
                  className="card-img-top"
                  alt={type.name}
                  style={{ objectFit: "cover", height: "320px" }}
                />
              </div>

              {/* Body */}
              <div className="card-body text-center">
                <h5 className="card-title fw-semibold">{type.name}</h5>

                {/* ADMIN ACTIONS */}
                {isAdmin && (
                  <div className="d-flex justify-content-center gap-2 mt-3">
                    <button
                      className="btn btn-outline-primary btn-sm"
                      onClick={() =>
                        navigate(`/type/edit/${type._id}`, {
                          state: { type },
                        })
                      }
                    >
                      Edit
                    </button>

                    <button
                      className="btn btn-outline-danger btn-sm"
                      onClick={() => handleDelete(type._id)}
                    >
                      Delete
                    </button>
                  </div>
                )}
              </div>

            </div>
          </div>
        ))}
      </div>

      {/* Add Product Button (ADMIN ONLY) */}
      {isAdmin && (
        <div className="d-flex justify-content-end mb-4">
          <button
            className="btn btn-dark d-flex align-items-center gap-2"
            onClick={() => navigate("/type", { state: { category } })}
          >
            <i className="bi bi-plus-lg"></i>
            Add Product
          </button>
        </div>
      )}
    </div>
  );
}
