import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../App.css";

export default function Product({ category, categories }) {
  const navigate = useNavigate();
  const [types, setTypes] = useState([]);

  useEffect(() => {
    const fetchTypes = async () => {
      try {
        const res = await fetch(
          `http://localhost:5000/api/type/category/${category}`
        );
        const data = await res.json();
        setTypes(data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchTypes();
  }, [category]);

  return (
    <div className="container py-5">
      {/* Header */}
      <header className="text-center mb-5">
        <h1 className="display-5 fw-bold">Shop by Category</h1>
        <p className="text-muted fs-5">
          Discover your favorite styles â€” timeless and effortless.
        </p>
      </header>

      {/* Types Grid */}
      <div className="row g-3 mb-5">
        {types.map((type) => (
          <div key={type._id} className="col-6 col-md-4 col-lg-3">
            <div
              className="card h-100 shadow-sm border-0"
              style={{ cursor: "pointer" }}
              onClick={() => navigate(`/product/${category}/${type.name}`)}
            >
              <img
                src={type.image}
                className="card-img-top"
                alt={type.name}
                style={{ objectFit: "cover", height: "150px" }}
              />
              <div className="card-body text-center">
                <h5 className="card-title">{type.name}</h5>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Add Product Button */}
      <div className="d-flex justify-content-end mb-4">
        <button
          className="btn btn-dark d-flex align-items-center gap-2"
          onClick={() => navigate("/type", { state: { category } })}
        >
          <i className="bi bi-plus-lg"></i>
          Add Product
        </button>
      </div>
    </div>
  );
}
