import React, { useState } from "react";
import { useContext } from "react";

 import alertContext from "../context/alert/alertContext";
export default function ForgotPassword({ setStep, setEmail}) {


  const [emailInput, setEmailInput] = useState("");
     const { showAlert } = useContext(alertContext);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const res = await fetch("http://localhost:5000/api/auth/forgotpassword", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email: emailInput }),
    });

    const data = await res.json();

    if (res.ok) {
      setEmail(emailInput);
      setStep(2);
   showAlert("Otp send to your mail",'success');
    } else {
         showAlert("something went wrong",'danger');
    }
  };

  return (
    <section
  className="gradient-custom d-flex align-items-center"
  style={{
    minHeight: "80vh",
    background: "linear-gradient(135deg, #6a11cb 0%, #2575fc 100%)",
  }}
>
  <div className="container py-5">
    <div className="row justify-content-center">
      <div className="col-12 col-md-8 col-lg-6">
        <div
          className="card shadow-lg mx-auto"
          style={{ borderRadius: "1rem", overflow: "hidden" }}
        >
          {/* Top gradient header */}
          <div
            className="p-4 text-center text-white"
            style={{
              background:
                "linear-gradient(to right, #6a11cb, #2575fc)",
            }}
          >
            <h2 className="fw-bold mb-2 text-uppercase">Forgot Password</h2>
            <p className="mb-0">
              Enter your email to receive a verification OTP
            </p>
          </div>

          <div className="card-body p-5 bg-light">
            <form onSubmit={handleSubmit}>
              <div className="form-group mb-4">
                <input
                  type="email"
                  className="form-control form-control-lg rounded-pill shadow-sm"
                  placeholder="Enter your email"
                  required
                  value={emailInput}
                  onChange={(e) => setEmailInput(e.target.value)}
                  style={{ fontSize: "1rem", padding: "1rem" }}
                />
              </div>

              <button
                type="submit"
                className="btn btn-primary btn-lg w-100 rounded-pill shadow"
                style={{
                  fontSize: "1.1rem",
                  padding: "0.75rem",
                  transition: "0.3s",
                }}
              >
                Send OTP
              </button>
            </form>

            <p className="text-center text-muted mt-4" style={{ fontSize: "0.9rem" }}>
              Remember your password?{" "}
              <span
                className="text-primary"
                style={{ cursor: "pointer" }}
                onClick={() => navigate("/login")}
              >
                Login
              </span>
            </p>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>

  );
}
