import React, { useState } from "react";
import { useContext } from "react";

 import alertContext from "../context/alert/alertContext";
export default function ForgotPassword({ setStep, setEmail}) {


  const [emailInput, setEmailInput] = useState("");
     const { showAlert } = useContext(alertContext);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const res = await fetch("http://localhost:5000/api/auth/forgotpassword", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email: emailInput }),
    });

    const data = await res.json();

    if (res.ok) {
      setEmail(emailInput);
      setStep(2);
   showAlert("Otp send to your mail",'success');
    } else {
         showAlert("something went wrong",'danger');
    }
  };

  return (
 <section
  className="gradient-custom d-flex justify-content-center align-items-center"
  style={{
    minHeight: "80vh",
    padding: "1rem",
    
  }}
>
 

        <div className="card shadow-lg mx-auto p-4">
      <h2 className="text-center fw-bold mb-3">Forgot Password</h2>
      <p className="text-center text-muted mb-4">
        Enter your email to receive an OTP
      </p>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          className="form-control form-control-lg mb-3 rounded-pill"
          placeholder="Enter your email"
          required
          value={emailInput}
          onChange={(e) => setEmailInput(e.target.value)}
        />
        <button className="btn btn-primary btn-lg w-100 rounded-pill">
          Send OTP
        </button>
      </form>
    </div>

      
    
</section>





  );
}
