import React, { useState } from "react";
import { useLocation } from "react-router-dom";

export default function AddType() {
  const location = useLocation();
  const categoryFromRoute = location.state?.category || "";

  const [formData, setFormData] = useState({
    name: "",
    category: categoryFromRoute,
    imgurl: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const payload = {
      name: formData.name.trim(),
      category: formData.category,
      imgurl: formData.imgurl.trim()
    };

    console.log(payload);
    // send payload to backend
  };

  return (
    <div className="container my-4 px-2">
      <div className="row justify-content-center">
        <div className="col-12 col-sm-10 col-md-8 col-lg-6">
          <div className="card shadow-sm border-0">

        
            <div className="card-header bg-light text-center">
              <h5 className="mb-0">Add Product Type</h5>
            </div>

           
            <div className="card-body">
              <form onSubmit={handleSubmit}>

                {/* Type Name */}
                <div className="mb-3">
                  <label className="form-label fw-semibold">Type Name</label>
                  <input
                    type="text"
                    className="form-control"
                    name="name"
                    placeholder="e.g. Shirt, T-Shirt"
                    value={formData.name}
                    onChange={handleChange}
                    required
                  />
                </div>

                {/* Category (locked) */}
                <div className="mb-3">
                  <label className="form-label fw-semibold">Category</label>
                  <select className="form-select" value={formData.category} disabled>
                    <option value={formData.category}>{formData.category}</option>
                  </select>
                </div>

                {/* Image URL (optional) */}
                <div className="mb-4">
                  <label className="form-label fw-semibold">Image URL (optional)</label>
                  <input
                    type="text"
                    className="form-control"
                    name="imgurl"
                    placeholder="https://example.com/image.jpg"
                    value={formData.imgurl}
                    onChange={handleChange}
                  />
                </div>

                <button type="submit" className="btn btn-dark w-100">
                  Add Type
                </button>

              </form>
            </div>

          </div>
        </div>
      </div>
    </div>
  );
}
