import React from 'react';
import './App.css';
import Navbar from './components/Navbar';
import Login from './pages/Login';
import Signup from './pages/Signup';
import Product from './components/Product';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { womenCategories, menCategories } from './data/categories';
import ProductItem from './components/ProductItem';
import ProductItemsPage from './pages/ProductItemsPage';
import ForgotPasswordFlow from './components/ForgotPasswordFlow';

import  { useContext, useState } from "react";
import AlertState from './context/alert/StateAlert';
import alertContext from './context/alert/alertContext';

import Alert from "./components/Alert";
import Cart from './pages/Cart';
import { CartProvider } from "./context/cart/CartState";
import Checkout from './pages/Checkout';
import Confirmation from './pages/Confirmation';
import ProductPage from './admin/ProductPage';
import AuthState from "./context/auth/AuthState"; 
function App() {

  return (
      <AuthState>
        <CartProvider>
    <AlertState>
    <Router>
      <Navbar />
<ProductPage/>


           <Alert alert={alert} />

      <ProductItem/>
<Cart/>
      <Routes>
        <Route path="/signup" element={<Signup />} />
        <Route path="/login" element={<Login />} />
        <Route path="/women" element={<Product category="women" categories={womenCategories} />} />

        <Route path="/men" element={<Product category="Men"  categories={menCategories} />} />
      <Route path="/product/:category/:type" element={<ProductItemsPage/>} />
       <Route path="/forgot-password" element={< ForgotPasswordFlow/>} />
          <Route path="/checkout" element={<Checkout />} />
      <Route path="/confirmation" element={<Confirmation/>} />
           <Route path="/product" element={<Product/>} />

      </Routes>
    </Router>
   </AlertState>
   </CartProvider>
   </AuthState>
  );
}

export default App;
