import React, { useEffect, useState } from "react";

export default function Cart() {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/cart/fetchcart", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "auth-token": localStorage.getItem("token"),
        },
      });
      const data = await res.json();
      setCartItems(data.items || []);
    } catch (error) {
      console.error("Error fetching cart:", error);
    } finally {
      setLoading(false);
    }
  };
const addbutton = async (item,qty) => {


  const cartItem = {
    productId: item.product._id,
    color: item.variant.color.toLowerCase(),
    size: item.size,
    image: item.variant.image,
    quantity: qty,
    price: item.price,
  };

  try {
    const res = await fetch("http://localhost:5000/api/cart/addcart", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token": localStorage.getItem("token"),
      },
      body: JSON.stringify(cartItem),
    });

    const data = await res.json();

    if (data.success) {
      await fetchCart();
    const updatedProductRes = await fetch(
  `http://localhost:5000/api/product/fetchproduct?id=${item.product._id}`
);
const updatedProductData = await updatedProductRes.json();
       

    }
  } catch (err) {
    console.error(err);
  } 
};

  const totalItems = cartItems.reduce((sum, item) => sum + item.quantity, 0);
  const subtotal = cartItems.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  if (loading) {
    return <h3 className="text-center mt-5">Loading cart...</h3>;
  }

  return (
    <section className="h-100" style={{ backgroundColor: "#f8f9fa" }}>
      <div className="container py-4 py-lg-5">
        <div className="row justify-content-center">
          <div className="col-12">
            <div className="card rounded-4 shadow-sm">
              <div className="card-body p-0">
                <div className="row g-0">

                 
                  <div className="col-lg-8 col-12">
                    <div className="p-4 p-lg-5">
                      <div className="d-flex justify-content-between align-items-center mb-4">
                        <h2 className="fw-bold mb-0">Shopping Cart</h2>
                        <span className="text-muted">{totalItems} items</span>
                      </div>
                      <hr />

                      {cartItems.length === 0 && (
                        <p className="text-center text-muted">
                          Your cart is empty
                        </p>
                      )}

                      {cartItems.map((item) => (
                        <div key={item._id}>
                          <div className="row align-items-center gy-3 mb-4">

                          
                            <div className="col-md-2 col-4">
                              <img
                                src={item.variant.image}
                                alt={item.name}
                                className="img-fluid rounded"
                                style={{
                                  maxHeight: "90px",
                                  objectFit: "contain",
                                }}
                              />
                            </div>

                       
                            <div className="col-md-3 col-8">
                              <h6 className="mb-1">{item.name}</h6>
                              <small className="text-muted">
                                Color: {item.variant.color}
                              </small>
                              <br />
                              <small className="text-muted">
                                Size: {item.size}
                              </small>
                            </div>

                          
                            <div className="col-md-3 col-12 d-flex justify-content-center align-items-center">
                              <button onClick={() => addbutton(item,-1)} className="btn btn-outline-secondary btn-sm px-3">
                                −
                              </button>

                              <input
                                type="number"
                                value={item.quantity}
                                readOnly
                                className="form-control form-control-sm mx-2 text-center"
                                style={{ width: "60px" }}
                              />

                              <button  onClick={() => addbutton(item,1)} className="btn btn-outline-secondary btn-sm px-3">
                                +
                              </button>
                            </div>

                            <div className="col-md-2 col-6 text-md-end">
                              <strong>
                                € {(item.price * item.quantity).toFixed(2)}
                              </strong>
                            </div>

                           
                            <div className="col-md-2 col-6 text-end">
                              <button  className="btn btn-link text-danger fs-5">
                                ✕
                              </button>
                            </div>

                          </div>
                          <hr />
                        </div>
                      ))}

                      <a href="/" className="text-decoration-none text-dark">
                        ← Back to shop
                      </a>
                    </div>
                  </div>

          
                  <div className="col-lg-4 col-12 bg-light">
                    <div className="p-4 p-lg-5">
                      <h4 className="fw-bold mb-4">Summary</h4>
                      <hr />

                      <div className="d-flex justify-content-between mb-3">
                        <span>Items ({totalItems})</span>
                        <span>€ {subtotal.toFixed(2)}</span>
                      </div>

                      <h6 className="text-uppercase">Shipping</h6>
                      <select className="form-select mb-4">
                        <option value={5}>
                          Standard Delivery - €5.00
                        </option>
                        <option value={10}>
                          Express Delivery - €10.00
                        </option>
                      </select>

                      <h6 className="text-uppercase">Promo Code</h6>
                      <input
                        type="text"
                        className="form-control mb-4"
                        placeholder="Enter code"
                      />

                      <hr />

                      <div className="d-flex justify-content-between mb-4">
                        <strong>Total</strong>
                        <strong>€ {(subtotal + 5).toFixed(2)}</strong>
                      </div>

                      <button className="btn btn-dark w-100 btn-lg">
                        Checkout
                      </button>
                    </div>
                  </div>

                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}
