import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function CategoryTypes({ category }) {
  const [types, setTypes] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTypes = async () => {
      try {
        const res = await fetch(
          `http://localhost:5000/api/type/category/${category}`
        );
        const data = await res.json();
        setTypes(data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchTypes();
  }, [category]);

  return (
    <div className="container py-5">
      <h3>Types for {category}</h3>
      <div className="row g-3">
        {types.map((type) => (
          <div key={type._id} className="col-6 col-md-4 col-lg-3">
            <div
              className="card h-100 shadow-sm border-0"
              style={{ cursor: "pointer" }}
              onClick={() => navigate(`/product/${category}/${type.name}`)}
            >
              <img
                src={type.image}
                className="card-img-top"
                alt={type.name}
                style={{ objectFit: "cover", height: "150px" }}
              />
              <div className="card-body text-center">
                <h5 className="card-title">{type.name}</h5>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
