import React, { useState } from "react";
import "./ProductItem.css";

export default function ProductItem({ product }) {
  if (!product) return null;

  const { name, price, tag, rating = 5, variants = [] } = product;

  // State for selected variant and size
  const [selectedVariant, setSelectedVariant] = useState(variants[0]);
  const [selectedSize, setSelectedSize] = useState(
    variants[0]?.sizes[0]?.size || ""
  );

  const handleColorClick = (variant) => {
    setSelectedVariant(variant);
    setSelectedSize(variant.sizes[0]?.size || ""); // select first size by default
  };

  const handleSizeClick = (size) => {
    setSelectedSize(size);
  };

  return (
    <div className="col-sm-6 col-md-4 col-lg-3">
      <div className="card product-card h-100 border-0 shadow-sm">
        {/* Image */}
        <div className="position-relative">
          {selectedVariant && selectedVariant.images[0] && (
            <img
              src={selectedVariant.images[0]}
              className="card-img-top product-img"
              alt={name}
            />
          )}

          {tag && (
            <span
              className={`badge ${
                tag.toLowerCase() === "sale" ? "bg-danger" : "bg-dark"
              } position-absolute top-0 start-0 m-2`}
            >
              {tag}
            </span>
          )}
        </div>

        <div className="card-body text-center">
          <h5 className="card-title">{name}</h5>

          {/* Rating */}
          <div className="text-warning mb-2">
            {[...Array(rating)].map((_, i) => (
              <span key={i} className="fas fa-star"></span>
            ))}
          </div>

          <p className="fw-bold">${price}</p>

          {/* Colors */}
          <div className="d-flex justify-content-center mb-2">
            {variants.map((v, i) => (
              <span
                key={i}
                className={`color-circle mx-1 ${
                  selectedVariant.color === v.color ? "selected" : ""
                }`}
                style={{ backgroundColor: v.color }}
                title={v.color}
                onClick={() => handleColorClick(v)}
              />
            ))}
          </div>

          {/* Sizes */}
          <div className="d-flex justify-content-center gap-2 flex-wrap mb-2">
            {selectedVariant?.sizes.map((s, i) => (
              <span
                key={i}
                className={`size-box ${
                  selectedSize === s.size ? "selected" : ""
                } ${s.quantity === 0 ? "out-of-stock" : ""}`}
                onClick={() => s.quantity > 0 && handleSizeClick(s.size)}
                title={`Qty: ${s.quantity}`}
              >
                {s.size}
              </span>
            ))}
          </div>

          {/* Quantity Display */}
          {selectedSize && (
            <p className="text-muted mt-2">
              Available:{" "}
              {
                selectedVariant.sizes.find((s) => s.size === selectedSize)
                  .quantity
              }
            </p>
          )}
        </div>
      </div>
    </div>
  );
}
