import React, { useState } from "react";
import { useContext } from "react";
import '../App.css'

 import alertContext from "../context/alert/alertContext";
export default function ForgotPassword({ setStep, setEmail}) {


  const [emailInput, setEmailInput] = useState("");
     const { showAlert } = useContext(alertContext);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const res = await fetch("http://localhost:5000/api/auth/forgotpassword", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email: emailInput }),
    });

    const data = await res.json();

    if (res.ok) {
      setEmail(emailInput);
      setStep(2);
   showAlert("Otp send to your mail",'success');
    } else {
         showAlert("something went wrong",'danger');
    }
  };

  return (
<section
  className="gradient-custom d-flex justify-content-center align-items-center"
  style={{
    minHeight: "80vh",
    width: "100vw",            // Full width of the viewport
    background: "linear-gradient(135deg, #6a11cb 0%, #2575fc 100%)",
  }}
>
  <div
    className="w-100"
    style={{
      maxWidth: "600px",      // Card max width
      padding: "0 1rem",      // Some horizontal padding
    }}
  >
    <div
      className="card shadow-lg mx-auto"
      style={{ borderRadius: "1rem", overflow: "hidden" }}
    >
      <div className="card-body p-5 text-center">
        <h2 className="fw-bold mb-2 text-uppercase">Forgot Password</h2>
        <p className="text-muted mb-4">
          Enter your email to receive an OTP
        </p>
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            className="form-control form-control-lg mb-3 rounded-pill"
            placeholder="Enter your email"
            required
            value={emailInput}
            onChange={(e) => setEmailInput(e.target.value)}
          />
          <button className="btn btn-primary btn-lg w-100 rounded-pill">
            Send OTP
          </button>
        </form>
      </div>
    </div>
  </div>
</section>


  );
}
