import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function Product() {
  const navigate = useNavigate();

  const categories = [
    {
      name: "Tshirt",
      image: "https://media.everlane.com/image/upload/c_scale,dpr_1.5,f_auto,q_auto,w_auto/v1/i/287f132e_2c66.jpg", // replace with your image path
      link: "Tshirt",
    },
    {
      name: "Shirt",
      image: "https://media.everlane.com/image/upload/c_scale,dpr_1.5,f_auto,q_auto,w_auto/v1/i/287f132e_2c66.jpg", // replace with your image path
      link: "Shirt",
    },
    {
      name: "Pant",
      image: "https://media.everlane.com/image/upload/c_scale,dpr_1.5,f_auto,q_auto,w_auto/v1/i/287f132e_2c66.jpg", // replace with your image path
      link: "Pant",
    },
    {
      name: "Sweater",
      image: "https://media.everlane.com/image/upload/c_scale,dpr_1.5,f_auto,q_auto,w_auto/v1/i/287f132e_2c66.jpg", // replace with your image path
      link: "Sweater",
    },
  ];

  const handleClick = (type) => {
    navigate(`/products/${type}`);
  };

  return (
    <div className="px-6 py-10 max-w-7xl mx-auto">
      {/* Hero Section */}
      <header className="text-center mb-12">
        <h1 className="text-4xl font-semibold tracking-wide">
          Shop by Category
        </h1>
        <p className="mt-2 text-gray-600">
          Discover your favorite styles â€” timeless and effortless.
        </p>
      </header>

      {/* Category Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8">
        {categories.map((cat) => (
          <div
            key={cat.name}
            className="cursor-pointer group overflow-hidden rounded-lg"
            onClick={() => handleClick(cat.link)}
          >
            <img
              src={cat.image}
              alt={cat.name}
              className="w-full h-64 object-cover group-hover:scale-105 transform transition duration-500"
            />
            <div className="mt-3 text-center">
              <h2 className="text-xl font-medium">{cat.name}</h2>
              <span className="text-sm uppercase text-gray-500">
                Shop {cat.name.toLowerCase()}
              </span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
