import React, { useState } from "react";
import ForgotPassword from "./ForgotPassword";
import VerifyOtp from "./VerifyOtp";
import ResetPassword from "./ResetPassword";
import Alert from "./Alert";

export default function ForgotPasswordFlow() {
  const [step, setStep] = useState(1);
  const [email, setEmail] = useState("");

  const [alert, setAlert] = useState(null);
  const showAlert = (message, type) => {
    setAlert({ msg: message, type });
    setTimeout(() => setAlert(null), 2000); // hide after 2s
  };

  return (
    <div
      className="d-flex justify-content-center align-items-center"
      style={{ minHeight: "100vh", background: "#f5f5f5", padding: "20px" }}
    >
      <div
        className="card shadow-lg p-4 p-md-5"
        style={{
          width: "100%",
          maxWidth: "450px",
          borderRadius: "15px",
          backgroundColor: "#1a1a1a",
          color: "#fff",
        }}
      >
        {/* Alert */}
        {alert && <Alert alert={alert} />}

        {/* Step Components */}
        {step === 1 && (
          <ForgotPassword
            setStep={setStep}
            setEmail={setEmail}
            showAlert={showAlert}
          />
        )}
        {step === 2 && (
          <VerifyOtp email={email} setStep={setStep} showAlert={showAlert} />
        )}
        {step === 3 && <ResetPassword email={email} showAlert={showAlert} />}
      </div>
    </div>
  );
}
