import React, { useEffect, useState } from "react";

export default function Cart() {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/cart/fetchcart",{
          method: "GET",
    headers: {
      "Content-Type": "application/json",
      "auth-token": localStorage.getItem("token"),
    },
      });
      const data = await res.json();
      setCartItems(data.items || []);
    } catch (error) {
      console.error("Error fetching cart:", error);
    } finally {
      setLoading(false);
    }
  };

  const totalItems = cartItems.reduce((sum, item) => sum + item.quantity, 0);
  const subtotal = cartItems.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  if (loading) {
    return <h3 className="text-center mt-5">Loading cart...</h3>;
  }

  return (
    <>
      <section className="h-100" style={{ backgroundColor: "#f8f9fa" }}>
        <div className="container py-5 h-100">
          <div className="row justify-content-center align-items-center h-100">
            <div className="col-12">
              <div className="card rounded-4">
                <div className="card-body p-0">
                  <div className="row g-0">

                    {/* LEFT */}
                    <div className="col-lg-8">
                      <div className="p-5">
                        <div className="d-flex justify-content-between align-items-center mb-4">
                          <h1 className="fw-bold mb-0">Shopping Cart</h1>
                          <h6 className="text-muted mb-0">
                            {totalItems} items
                          </h6>
                        </div>
                        <hr />

                        {cartItems.length === 0 && (
                          <p className="text-center">Your cart is empty</p>
                        )}

                        {cartItems.map((item) => (
                          <div key={item._id}>
                            <div className="row align-items-center mb-4">
                              <div className="col-md-2">
                                <img
                                  src={item.variant.image}
                                  className="img-fluid rounded"
                                  alt={item.name}
                                />
                              </div>

                              <div className="col-md-3">
                                <h6 className="text-muted">{item.category}</h6>
                                   <h6 className="mb-0">Color:{item.variant.color}</h6>
                                <h6 className="mb-0">Size{item.size}</h6>
                              </div>

                              <div className="col-md-3 d-flex align-items-center">
                                <button className="btn btn-outline-secondary btn-sm">
                                  −
                                </button>

                                <input
                                  type="number"
                                  value={item.quantity}
                                  readOnly
                                  className="form-control form-control-sm mx-2 text-center"
                                  style={{ width: "60px" }}
                                />

                                <button className="btn btn-outline-secondary btn-sm">
                                  +
                                </button>
                              </div>

                              <div className="col-md-2 text-end">
                                <h6 className="mb-0">
                                  € {(item.price * item.quantity).toFixed(2)}
                                </h6>
                              </div>

                              <div className="col-md-2 text-end">
                                <button className="btn btn-link text-muted">
                                  ✕
                                </button>
                              </div>
                            </div>
                            <hr />
                          </div>
                        ))}

                        <div className="pt-4">
                          <a href="/" className="text-decoration-none text-dark">
                            ← Back to shop
                          </a>
                        </div>
                      </div>
                    </div>

                    {/* RIGHT */}
                    <div className="col-lg-4 bg-light">
                      <div className="p-5">
                        <h3 className="fw-bold mb-4">Summary</h3>
                        <hr />

                        <div className="d-flex justify-content-between mb-3">
                          <h5>Items ({totalItems})</h5>
                          <h5>€ {subtotal.toFixed(2)}</h5>
                        </div>

                        <h6 className="text-uppercase mb-2">Shipping</h6>
                        <select className="form-select mb-4">
                          <option value={5}>Standard Delivery - €5.00</option>
                          <option value={10}>Express Delivery - €10.00</option>
                        </select>

                        <h6 className="text-uppercase mb-2">Promo Code</h6>
                        <input
                          type="text"
                          className="form-control mb-4"
                          placeholder="Enter code"
                        />

                        <hr />

                        <div className="d-flex justify-content-between mb-4">
                          <h5>Total Price</h5>
                          <h5>€ {(subtotal + 5).toFixed(2)}</h5>
                        </div>

                        <button className="btn btn-dark w-100 btn-lg">
                          Checkout
                        </button>
                      </div>
                    </div>

                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
