import React, { useState } from "react";
import ForgotPassword from "./ForgotPassword";
import VerifyOtp from "./VerifyOtp";
import ResetPassword from "./ResetPassword";
import Alert from "./Alert";

export default function ForgotPasswordFlow() {
  const [alert, setAlert] = useState(null); // state for Alert
  const [step, setStep] = useState(1);
  const [email, setEmail] = useState("");

  // Function to show alert
  const showAlert = (message, type) => {
    setAlert({ msg: message, type });
    setTimeout(() => setAlert(null), 2000); // auto-hide after 2s
  };

  return (
    <div className="container mt-5" style={{ maxWidth: "400px" }}>
      {/* Pass alert state as props.alert to match your Alert component */}
      <Alert alert={alert} />

      {/* Step-wise rendering */}
      {step === 1 && (
        <ForgotPassword
          setStep={setStep}
          setEmail={setEmail}
          showAlert={showAlert}
        />
      )}
      {step === 2 && (
        <VerifyOtp
          email={email}
          setStep={setStep}
          showAlert={showAlert}
        />
      )}
      {step === 3 && <ResetPassword email={email} showAlert={showAlert} />}
    </div>
  );
}
