// pages/Dashboard.js
import React, { useEffect, useState } from "react";

import DashboardCard from "../components/DashboardCard";
import Customerview from "./Custormerview";

export default function Dashboard() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/order/all")
      .then(res => res.json())
      .then(data => {
        if (data.success) setOrders(data.orders);
      });
  }, []);

  const totalOrders = orders.length;
  const pending = orders.filter(o => o.orderStatus === "Pending").length;
  const delivered = orders.filter(o => o.orderStatus === "Delivered").length;
  const cancelled = orders.filter(o => o.orderStatus === "Cancelled").length;

  const revenue = orders
    .filter(o => o.orderStatus === "Delivered")
    .reduce((sum, o) => sum + o.total, 0);

  return (
    <div className="d-flex">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className="flex-grow-1 bg-light min-vh-100 p-4">
        <Topbar />

        {/* Stats */}
        <div className="row g-3 mb-4">
          <DashboardCard title="Total Orders" value={totalOrders} color="primary" />
          <DashboardCard title="Pending" value={pending} color="warning" />
          <DashboardCard title="Delivered" value={delivered} color="success" />
          <DashboardCard title="Cancelled" value={cancelled} color="danger" />
          <DashboardCard title="Revenue (â‚¹)" value={revenue} color="dark" />
        </div>

        {/* Orders */}
        <div className="card shadow-sm">
          <div className="card-body">
            <h5 className="mb-3">Recent Orders</h5>
            <AdminOrders />
          </div>
        </div>
      </div>
    </div>
  );
}
