import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

export default function EditProduct() {
  const navigate = useNavigate();
  const { state: product } = useLocation();

  const [name, setName] = useState(product.name);
  const [variants, setVariants] = useState(product.variants);

  if (!product) return <p>No product data</p>;
const addColor = () => {
  setVariants([
    ...variants,
    {
      color: "",
      images: "",
      sizes: []
    }
  ]);
};
const deleteColor = (index) => {
  setVariants(variants.filter((_, i) => i !== index));
};
const addSize = (variantIndex) => {
  const updated = [...variants];
  updated[variantIndex].sizes.push({ size: "", quantity: 0 });
  setVariants(updated);
};
const deleteSize = (variantIndex, sizeIndex) => {
  const updated = [...variants];
  updated[variantIndex].sizes =
    updated[variantIndex].sizes.filter((_, i) => i !== sizeIndex);
  setVariants(updated);
};


 return (
  <div className="container my-4">
    <h3>Edit Product</h3>

    <input
      className="form-control mb-3"
      value={name}
      onChange={(e) => setName(e.target.value)}
      placeholder="Product name"
    />

    {variants.map((variant, vIndex) => (
      <div key={vIndex} className="border p-3 mb-3 rounded">

        <div className="d-flex gap-2 mb-2">
          <input
            className="form-control"
            value={variant.color}
            placeholder="Color"
            onChange={(e) => {
              const updated = [...variants];
              updated[vIndex].color = e.target.value;
              setVariants(updated);
            }}
          />

          <button
            className="btn btn-danger"
            onClick={() => deleteColor(vIndex)}
          >
            Delete Color
          </button>
        </div>

     
        <input
          className="form-control mb-2"
          value={variant.images}
          placeholder="Image URL"
          onChange={(e) => {
            const updated = [...variants];
            updated[vIndex].images = e.target.value;
            setVariants(updated);
          }}
        />

     
        {variant.sizes.map((s, sIndex) => (
          <div key={sIndex} className="d-flex gap-2 mb-2">
            <input
              className="form-control"
              placeholder="Size"
              value={s.size}
              onChange={(e) => {
                const updated = [...variants];
                updated[vIndex].sizes[sIndex].size = e.target.value;
                setVariants(updated);
              }}
            />

            <input
              type="number"
              className="form-control"
              placeholder="Qty"
              value={s.quantity}
              onChange={(e) => {
                const updated = [...variants];
                updated[vIndex].sizes[sIndex].quantity = Number(e.target.value);
                setVariants(updated);
              }}
            />

            <button
              className="btn btn-outline-danger"
              onClick={() => deleteSize(vIndex, sIndex)}
            >
              âœ•
            </button>
          </div>
        ))}

        <button
          className="btn btn-outline-secondary"
          onClick={() => addSize(vIndex)}
        >
          + Add Size
        </button>
      </div>
    ))}

    <button className="btn btn-outline-dark mb-3" onClick={addColor}>
      + Add Color
    </button>

    <div className="d-flex gap-2">
      <button className="btn btn-success">
        Save Changes
      </button>

      <button
        className="btn btn-secondary"
        onClick={() => navigate(-1)}
      >
        Cancel
      </button>
    </div>
  </div>
);
}
