import React from "react";
import { useNavigate } from "react-router-dom";

export default function Product() {
  const navigate = useNavigate();

  const categories = [
    {
      name: "Tshirt",
      image:
        "https://media.everlane.com/image/upload/c_scale,dpr_1.5,f_auto,q_auto,w_auto/v1/i/287f132e_2c66.jpg",
      link: "Tshirt",
    },
    {
      name: "Shirt",
      image:
        "https://media.everlane.com/image/upload/c_scale,dpr_1.5,f_auto,q_auto,w_auto/v1/i/287f132e_2c66.jpg",
      link: "Shirt",
    },
    {
      name: "Pant",
      image:
        "https://media.everlane.com/image/upload/c_scale,dpr_1.5,f_auto,q_auto,w_auto/v1/i/287f132e_2c66.jpg",
      link: "Pant",
    },
    {
      name: "Sweater",
      image:
        "https://media.everlane.com/image/upload/c_scale,dpr_1.5,f_auto,q_auto,w_auto/v1/i/287f132e_2c66.jpg",
      link: "Sweater",
    },
  ];

  const handleClick = (type) => {
    navigate(`/products/${type}`);
  };

  return (
    <div className="px-6 py-10 max-w-7xl mx-auto">
      {/* Hero Section */}
      <header className="text-center mb-12">
        <h1 className="text-5xl font-extrabold tracking-tight text-gray-900">
          Shop by Category
        </h1>
        <p className="mt-3 text-gray-600 text-lg">
          Discover your favorite styles â€” timeless and effortless.
        </p>
      </header>

      {/* Category Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8">
        {categories.map((cat) => (
          <div
            key={cat.name}
            className="relative cursor-pointer overflow-hidden rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-500 group"
            onClick={() => handleClick(cat.link)}
          >
            <img
              src={cat.image}
              alt={cat.name}
              className="w-full h-72 object-cover transition-transform duration-500 group-hover:scale-110"
            />
            {/* Overlay */}
            <div className="absolute inset-0 bg-black bg-opacity-30 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-500">
              <button className="px-6 py-2 bg-white text-gray-900 font-semibold rounded-full hover:bg-gray-200 transition">
                Shop Now
              </button>
            </div>
            <div className="mt-4 text-center p-3">
              <h2 className="text-2xl font-semibold text-gray-900">{cat.name}</h2>
              <p className="text-sm uppercase text-gray-500">
                Explore {cat.name.toLowerCase()}
              </p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
