import React, { useEffect, useState } from "react";
import dayjs from "dayjs";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";
import { Line, Doughnut } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend
);

export default function Dashboard() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchOrders = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/order/all");
      const data = await res.json();
      if (data.success) setOrders(data.orders);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  /* ================== CALCULATIONS ================== */
  const today = dayjs().startOf("day");

  const deliveredOrders = orders.filter(o => o.orderStatus === "Delivered");

  const totalRevenue = orders
    .filter(o => o.orderStatus === "Delivered")
    .reduce((sum, o) => sum + Number(o.total || 0), 0);

  const todaysSales = orders
    .filter(
      o =>
        o.orderStatus !== "Cancelled" &&
        dayjs(o.createdAt).startOf("day").isSame(today)
    )
    .reduce((sum, o) => sum + Number(o.total || 0), 0);

  const totalOrders = orders.length;
  const pendingOrders = orders.filter(o => o.orderStatus === "Pending").length;

  const monthlyRevenue = deliveredOrders
    .filter(o => dayjs(o.createdAt).isSame(dayjs(), "month"))
    .reduce((sum, o) => sum + o.total, 0);

  const avgOrderValue =
    deliveredOrders.length > 0
      ? Math.round(
          deliveredOrders.reduce((s, o) => s + o.total, 0) / deliveredOrders.length
        )
      : 0;

  const newCustomers = new Set(
    orders
      .filter(o => dayjs().diff(dayjs(o.createdAt), "day") <= 7)
      .map(o => o.customer.email)
  ).size;

  const visitors = 1000; // example
  const conversionRate = ((totalOrders / visitors) * 100).toFixed(2);

  /* ================== CHART DATA ================== */

  const ordersByDay = Array(7).fill(0);
  const labels = [];

  // Last 7 days labels
  for (let i = 6; i >= 0; i--) {
    labels.push(dayjs().subtract(i, "day").format("ddd"));
  }

  // Count orders per day
  orders.forEach(o => {
    const orderDate = dayjs(o.createdAt).startOf("day");
    const diff = today.diff(orderDate, "day");
    if (diff >= 0 && diff < 7) {
      ordersByDay[6 - diff]++; // 6 = today, 0 = 6 days ago
    }
  });

  const ordersLineChart = {
    labels,
    datasets: [
      {
        label: "Orders",
        data: ordersByDay,
        borderColor: "#0d6efd",
        backgroundColor: "rgba(13,110,253,0.15)",
        tension: 0.4,
        fill: true,
      },
    ],
  };

  const statusChart = {
    labels: ["Pending", "Delivered", "Cancelled"],
    datasets: [
      {
        data: [
          pendingOrders,
          deliveredOrders.length,
          orders.filter(o => o.orderStatus === "Cancelled").length,
        ],
        backgroundColor: ["#ffc107", "#198754", "#dc3545"],
      },
    ],
  };

  /* ================== UI ================== */
  if (loading) return <p className="text-center mt-5">Loading dashboard...</p>;

  return (
    <div className="container p-4">
      <h2 className="mb-4">Admin Dashboard</h2>

      {/* STATS */}
      <div className="row g-4 mb-4">
       <StatCard
  icon={<i className="bi bi-currency-rupee" style={{ fontSize: "2rem" }}></i>}
  title="Today's Sales"
  value={`₹${todaysSales}`}
  color="primary"
/>

        <StatCard title="Total Orders" value={totalOrders} color="dark" />
        <StatCard title="Pending Orders" value={pendingOrders} color="warning" />
        <StatCard title="Total Revenue" value={`₹${totalRevenue}`} color="success" />
      </div>

      {/* CHARTS */}
      <div className="row g-4">
        <div className="col-md-8">
          <div className="card shadow-sm">
            <div className="card-body">
              <h5 className="mb-3">Orders (Last 7 Days)</h5>
              <Line data={ordersLineChart} />
            </div>
          </div>
        </div>

        <div className="col-md-4">
          <div className="card shadow-sm">
            <div className="card-body">
              <h5 className="mb-3">Order Status</h5>
              <Doughnut data={statusChart} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

/* ================== STAT CARD ================== */
const StatCard = ({ title, value, color }) => (
  <div className="col-md-3 col-sm-6">
    <div className={`card text-white bg-${color} shadow-sm`}>
      <div className="card-body text-center">
        <h6 className="mb-1">{title}</h6>
        <h4 className="mb-0">{value}</h4>
      </div>
    </div>
  </div>
);
