import React from "react";
import { useNavigate } from "react-router-dom";




export default function Product() {
  const navigate = useNavigate();

  // Just using "Sweater" repeated 5 times for 5 rows
  const categories = Array(5).fill({
    name: "Sweater",
    image:
      "https://media.everlane.com/image/upload/c_scale,dpr_1.5,f_auto,q_auto,w_auto/v1/i/287f132e_2c66.jpg",
    link: "Sweater",
  });

  const handleClick = (type) => {
    navigate(`/products/${type}`);
  };

  return (
    <div className="container-fluid p py-5">
      {/* Hero Section */}
      <header className="text-center mb-5">
        <h1 className="display-5 fw-bold">Shop by Category</h1>
        <p className="text-muted fs-5">
          Discover your favorite styles â€” timeless and effortless.
        </p>
      </header>

      {/* Category Rows */}
      <div className="row g-4">
        {categories.map((cat, index) => (
          <div key={index} className="col-12">
            <div
              className="card h-100 shadow-sm border-0"
              style={{ cursor: "pointer" }}
              onClick={() => handleClick(cat.link)}
            >
              <div className="position-relative overflow-hidden">
                <img
                  src={cat.image}
                  className="card-img-top img-fluid"
                  alt={cat.name}
                  style={{ transition: "transform 0.3s" }}
                />
                <div className="position-absolute top-0 start-0 w-100 h-100 d-flex justify-content-center align-items-center bg-dark bg-opacity-25 opacity-0 hover-opacity">
                  <button className="btn btn-light fw-bold">Shop Now</button>
                </div>
              </div>
              <div className="card-body text-center">
                <h5 className="card-title">{cat.name}</h5>
                <p className="card-text text-uppercase text-muted">
                  Explore {cat.name.toLowerCase()}
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Custom CSS for hover effect */}
      <style>
        {`
          .hover-opacity:hover {
            opacity: 1 !important;
            transition: opacity 0.3s;
          }
          .card img:hover {
            transform: scale(1.05);
          }
        `}
      </style>
    </div>
  );
}
