import React, { useState } from "react";
import ForgotPassword from "./ForgotPassword";
import VerifyOtp from "./VerifyOtp";
import ResetPassword from "./ResetPassword";
import Alert from "./Alert";

export default function ForgotPasswordFlow() {
  const [alert, setAlert] = useState(null);
  const [step, setStep] = useState(1);
  const [email, setEmail] = useState("");

  // Function to show alert messages
  const showAlert = (message, type) => {
    setAlert({ msg: message, type });
    setTimeout(() => setAlert(null), 2000); // auto-hide after 2 seconds
  };

   return (
    <div className="container mt-5" style={{ maxWidth: "400px" }}>
      {step === 1 && <ForgotPassword setStep={setStep} setEmail={setEmail} showAlert={showAlert} />}
      {step === 2 && <VerifyOtp email={email} setStep={setStep} showAlert={showAlert} />}
      {step === 3 && <ResetPassword email={email} showAlert={showAlert} />}
    </div>
  );
}
