import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ProductItem from "../components/ProductItem";

export default function ProductItemsPage() {
  const { category, type } = useParams();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await fetch(
        `http://localhost:5000/api/product/fetchproduct?category=${category}&type=${type}`
        );
        const data = await res.json();
        if (data.success) {
          setProducts(data.products);
        }
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, [category, type]);

  if (loading) return <p className="text-center mt-5">Loading products...</p>;
  if (!products.length) return <p className="text-center mt-5">No products found.</p>;

  return (
    <div className="container py-5">
      <h2 className="mb-4 text-center">
        {type}s for {category}
      </h2>
      <div className="row g-4">
        {products.map((product) => (
          <ProductItem key={product._id} product={product} />
        ))}
      </div>
    </div>
  );
}
