import React, { useState } from "react";

export default function ProductPage({ onSubmit }) {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [category, setCategory] = useState("Men");
  const [color, setColor] = useState("");
  const [imgurl, setImgurl] = useState("");
  const [size, setSize] = useState("");
  const [quantity, setQuantity] = useState("");
  const [description, setDescription] = useState("");
   const [type, setType] = useState("");

 
  const handleSubmit = (e) => {
    e.preventDefault();
    const size={
        size,
        quantity
        
    }
    const variants={
        color,
        imgurl,
        sizes
    }
    const productData = {
      name,
      description,
      price: Number(price),
      category,
      type,
      variants,
    };}
  return (
    <div className="d-flex justify-content-center my-3 px-2">
      <form
        onSubmit={handleSubmit}
        className="p-3 border rounded shadow-sm w-100"
        style={{ maxWidth: "600px" }} // ✅ limits width on large screens
      >
        <h3 className="mb-4 text-center">Add New Product</h3>

        <div className="mb-3">
          <label className="form-label">Product Name</label>
          <input
            type="text"
            className="form-control"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Description</label>
          <textarea
            className="form-control"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
            rows={2}
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Price (₹)</label>
          <input
            type="number"
            className="form-control"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Category</label>
          <select
            className="form-select"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            <option>Men</option>
            <option>Women</option>
            <option>Kids</option>
          </select>
        </div>

  <div className="mb-3">
          <label className="form-label">Type</label>
          <select
            className="form-select"
            value={category}
            onChange={(e) => setType(e.target.value)}
          >
   
            <option>Shirt</option>
            <option>Pant</option>
            <option>Sweater</option>
              <option>Tshirt</option>
          </select>
        </div>
   <div className="mb-3 p-3 border rounded">
           <label className="form-label">Image URL</label>
              <input
                type="text"
                className="form-control"
                value={imgurl}
                onChange={(e) => setImgurl(e.target.value)}
                required
              />
        </div>
         <div className="mb-3 p-3 border rounded">
              <label className="form-label">Size</label>
              <input
                type="text"
                className="form-control"
                placeholder="S, M, L"
                value={size}
                onChange={(e) => setSize(e.target.value)}
                required
              /></div>
              <div className="mb-3 p-3 border rounded">
          
              <label className="form-label">Quantity</label>
              <input
                type="number"
                className="form-control"
                placeholder="0"
                value={quantity}
                onChange={(e) => setQuantity(e.target.value)}
                required
                min={0}
              />
       </div>

        <button type="submit" className="btn btn-dark w-100 mt-3">
          Submit Product
        </button>
      </form>
    </div>
  );
}
