import React, { useState } from "react";
import { useCart } from "../context/cart/CartState";
import "./ProductItem.css";

export default function ProductItem({ product }) {
  const { cartItems, addToCart } = useCart();
    const [selectedVariant, setSelectedVariant] = useState(variants[0]);
  const [selectedSize, setSelectedSize] = useState("");

  // 1️⃣ Guard against undefined product or empty variants
  if (!product) return null;
  const variants = product.variants || [];
  if (!variants.length) return null;



  const handleColorClick = (variant) => {
    setSelectedVariant(variant);
    setSelectedSize("");
  };

  const handleSizeClick = (size) => {
    setSelectedSize(size);
  };

  const handleAddToCart = () => {
    if (!selectedVariant || !selectedSize) return;

    const cartItem = {
      productId: product._id,
      color: selectedVariant.color,
      size: selectedSize,
      image: selectedVariant.images,
      price: product.price,
    };

    addToCart(cartItem, 1); 
  };

  const variantSizeObj = selectedVariant?.sizes.find(s => s.size === selectedSize);
  const inCartQty = cartItems.find(
    c => c.productId === product._id &&
         c.color === selectedVariant.color &&
         c.size === selectedSize
  )?.quantity || 0;

  const availableQty = (variantSizeObj?.quantity || 0) - inCartQty;

  return (
    <div className="col-sm-6 col-md-4 col-lg-3">
      <div className="card product-card h-100 border-0 shadow-sm">
        <div className="position-relative">
          <img
            src={selectedVariant?.images || ""}
            className="card-img-top product-img"
            alt={product.name}
            style={{ opacity: product.isActive ? 1 : 0.5 }}
          />
          {selectedSize && availableQty <= 0 && (
            <span className="badge bg-danger position-absolute top-50 start-50 translate-middle">
              Not Available
            </span>
          )}
        </div>

        <div className="card-body text-center">
          <h5 className="card-title">{product.name}</h5>
          <p className="fw-bold">₹{product.price}</p>

          <div className="d-flex justify-content-center mb-2">
            {variants.map((v, i) => (
              <span
                key={i}
                className={`color-circle mx-1 ${selectedVariant.color === v.color ? "selected" : ""}`}
                style={{ backgroundColor: v.color }}
                onClick={() => handleColorClick(v)}
              />
            ))}
          </div>

          <div className="d-flex justify-content-center gap-2 flex-wrap mb-2">
            {selectedVariant?.sizes.map((s, i) => (
              <span
                key={i}
                className={`size-box ${selectedSize === s.size ? "selected" : ""} ${s.quantity === 0 ? "out-of-stock" : ""}`}
                onClick={() => handleSizeClick(s.size)}
                style={{ cursor: "pointer" }}
              >
                {s.size}
              </span>
            ))}
          </div>

          {selectedVariant && selectedSize && (
            <span className="badge bg-secondary mt-2">
              Available: {availableQty > 0 ? availableQty : 0}
            </span>
          )}

          <button
            className="btn btn-dark w-100 mt-2"
            disabled={!selectedVariant || !selectedSize || availableQty <= 0}
            onClick={handleAddToCart}
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}
