// ProductItem.js
import React from 'react';

export default function ProductItem({ product }) {
  const { name, image, price, tag, rating = 5 } = product;

  return (
    <div className="col-lg-3 col-sm-6 d-flex flex-column align-items-center justify-content-center product-item my-3">
      <div className="product position-relative">
        <img src={image} alt={name} className="img-fluid" />
        <ul className="d-flex align-items-center justify-content-center list-unstyled icons position-absolute">
          <li className="icon"><span className="fas fa-expand-arrows-alt"></span></li>
          <li className="icon mx-3"><span className="far fa-heart"></span></li>
          <li className="icon"><span className="fas fa-shopping-bag"></span></li>
        </ul>
      </div>

      {tag && <div className={`tag ${tag === "sale" ? "bg-red" : "bg-black"}`}>{tag}</div>}

      <div className="title pt-4 pb-1">{name}</div>

      <div className="d-flex align-content-center justify-content-center">
        {[...Array(rating)].map((_, i) => (
          <span key={i} className="fas fa-star"></span>
        ))}
      </div>

      <div className="price">${price}</div>
    </div>
  );
}
