import React from "react";
import "../ProductItem.css";

export default function ProductItem({ product }) {
  if (!product) return null;

  const {
    name,
    image,
    price,
    tag,
    rating = 5,
    colors = [],
    sizes = []
  } = product;

  return (
    <div className="col-sm-6 col-md-4 col-lg-3">
      <div className="card product-card h-100 border-0 shadow-sm">

        <div className="position-relative">
          <img src={image} className="card-img-top product-img" alt={name} />

          {tag && (
            <span className={`badge ${tag === "sale" ? "bg-danger" : "bg-dark"} position-absolute top-0 start-0 m-2`}>
              {tag}
            </span>
          )}
        </div>

        <div className="card-body text-center">
          <h5>{name}</h5>

          <div className="text-warning mb-2">
            {[...Array(rating)].map((_, i) => (
              <span key={i} className="fas fa-star"></span>
            ))}
          </div>

          <p className="fw-bold">${price}</p>

          <div className="d-flex justify-content-center mb-2">
            {colors.map((c, i) => (
              <span key={i} className="color-circle mx-1" style={{ backgroundColor: c }} />
            ))}
          </div>

          <div className="d-flex justify-content-center gap-2">
            {sizes.map((s, i) => (
              <span key={i} className="size-box">{s}</span>
            ))}
          </div>
        </div>

      </div>
    </div>
  );
}
