import React, { useEffect, useState } from "react";
import dayjs from "dayjs";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";
import { Line, Doughnut } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend
);

const StatCard = ({ title, value, color, icon }) => (
  <div className="col-md-3 col-sm-6">
    <div
      className="card bg-white shadow-sm"
      style={{ borderLeft: `5px solid ${color}`, minHeight: "100px" }}
    >
      <div className="card-body d-flex align-items-center">
        {icon && <div className="me-3">{icon}</div>}
        <div>
          <h6 className="mb-1 text-dark">{title}</h6>
          <h4 className="mb-0 text-dark">{value}</h4>
        </div>
      </div>
    </div>
  </div>
);
export default function Dashboard() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchOrders = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/order/all");
      const data = await res.json();
      if (data.success) setOrders(data.orders);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);


  const today = dayjs().startOf("day");
  const deliveredOrders = orders.filter((o) => o.orderStatus === "Delivered");
  const totalRevenue = deliveredOrders.reduce(
    (sum, o) => sum + Number(o.total || 0),
    0
  );
  const todaysSales = orders
    .filter(
      (o) =>
        o.orderStatus !== "Cancelled" &&
        dayjs(o.createdAt).startOf("day").isSame(today)
    )
    .reduce((sum, o) => sum + Number(o.total || 0), 0);
  const totalOrders = orders.length;
  const pendingOrders = orders.filter((o) => o.orderStatus === "Pending")
    .length;

  const ordersByDay = Array(7).fill(0);
  const labels = [];
  for (let i = 6; i >= 0; i--) {
    labels.push(dayjs().subtract(i, "day").format("ddd"));
  }
  orders.forEach((o) => {
    const orderDate = dayjs(o.createdAt).startOf("day");
    const diff = today.diff(orderDate, "day");
    if (diff >= 0 && diff < 7) ordersByDay[6 - diff]++;
  });
  const ordersLineChart = {
    labels,
    datasets: [
      {
        label: "Orders",
        data: ordersByDay,
        borderColor: "#0d6efd",
        backgroundColor: "rgba(13,110,253,0.15)",
        tension: 0.4,
        fill: true,
      },
    ],
  };
  const statusChart = {
    labels: ["Pending", "Delivered", "Cancelled"],
    datasets: [
      {
        data: [
          pendingOrders,
          deliveredOrders.length,
          orders.filter((o) => o.orderStatus === "Cancelled").length,
        ],
        backgroundColor: ["#ffc107", "#198754", "#dc3545"],
      },
    ],
  };

  if (loading) return <p className="text-center mt-5">Loading dashboard...</p>;

  return (
    <div className="d-flex" style={{ minHeight: "100vh" }}>
      
      <nav
        className="bg-light text-black p-3"
        style={{ width: "250px", minHeight: "100vh" }}
      >
       
        <ul className="nav flex-column">
          <li className="nav-item mb-2">
            <a href="#" className="nav-link text-white">
              <i className="bi bi-speedometer2 me-2"></i>Dashboard
            </a>
          </li>
          <li className="nav-item mb-2">
            <a href="#" className="nav-link text-white">
              <i className="bi bi-bag-fill me-2"></i>Orders
            </a>
          </li>
          <li className="nav-item mb-2">
            <a href="#" className="nav-link text-white">
              <i className="bi bi-people-fill me-2"></i>Customers
            </a>
          </li>
         
        </ul>
      </nav>

     
      <div className="flex-grow-1 p-4 mt-4">
        <h2 className="mb-4"> Dashboard</h2>

        {/* STAT CARDS */}
        <div className="row g-4 mb-4 mt-4">
          <StatCard
            icon={
              <i
                className="bi bi-currency-rupee"
                style={{ fontSize: "2.5rem", color: "#0d6efd" }}
              ></i>
            }
            title="Today's Sales"
            value={`₹${todaysSales}`}
            color="#0d6efd"
          />
          <StatCard
            icon={
              <i
                className="bi bi-card-checklist"
                style={{ fontSize: "2.5rem", color: "#6f42c1" }}
              ></i>
            }
            title="Total Orders"
            value={totalOrders}
            color="#6f42c1"
          />
          <StatCard
            icon={
              <i
                className="bi bi-hourglass-split"
                style={{ fontSize: "2.5rem", color: "#ffc107" }}
              ></i>
            }
            title="Pending Orders"
            value={pendingOrders}
            color="#ffc107"
          />
          <StatCard
            icon={
              <i
                className="bi bi-cash-stack"
                style={{ fontSize: "2.5rem", color: "#198754" }}
              ></i>
            }
            title="Total Revenue"
            value={`₹${totalRevenue}`}
            color="#198754"
          />
        </div>

       
        <div className="row g-4">
          <div className="col-md-8">
            <div className="card shadow-sm">
              <div className="card-body">
                <h5 className="mb-3">Orders (Last 7 Days)</h5>
                <Line data={ordersLineChart} />
              </div>
            </div>
          </div>

          <div className="col-md-4">
            <div className="card shadow-sm">
              <div className="card-body">
                <h5 className="mb-3">Order Status</h5>
                <Doughnut data={statusChart} />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
